  #{teachingtimeweek,jdbcType=CHAR}, #{teachingtimebegintime,jdbcType=TIME}, #{teachingtimeendtime,jdbcType=TIME}, 
  #{teachingtimestandbyone,jdbcType=VARCHAR}, #{teachingtimestandbytwo,jdbcType=INTEGER}
  )

mapper.select:
